from tkinter import *root = Tk()root.title("sales app")root.geometry("500x500")Month1 = {"jan" , "feb" , "march" , "april" , "may" , "june" , "july" , "august" , "sept" , "oct" , "nov" , "dec"}Money1 = {"20000" , "45000" , "78000" , "970000" , "12000" , "456000" , "65000" , "54000" , "10000" , "30000" , "70000" , "90000"}label_month = Label(root , text = "Months: {jan , feb , march , april , may , june , july , august , sept , oct , nov , dec}")label_month.pack()label_money = Label(root , text = "profit : {20000 , 45000 , 78000 , 970000 , 12000 , 456000 , 65000 , 54000 , 10000 , 30000 , 70000 , 90000}")label_money.pack()highestprof = ""lowestprof = ""def money():    global highestprof    highestprof = "The most profitable month was in the month of june with 456000"    Showmax = Button(root , text = "Show Maximum Profit" , command = money)Showmax.pack()Maxproflabel = Label(root)Maxproflabel["text"] = str(highestprof)Maxproflabel.pack()def nomoney():    global lowestprof    lowestprof = "Lowest profit was obtained in the month of sept with 10000"    Showmin = Button(root , text = "Show Maximum Profit" , command = nomoney)Showmin.pack()Minproflabel = Label(root)Minproflabel["text"] = str(lowestprof)Minproflabel.pack()root.mainloop()